        var choice = $(""#coffeeMachineSelector"").val();
        //reset open/closed elements to starting defaults
        $(""#allDone"").hide();
        $(""#newDrink"").show();
        $(""#coffeeMug"").hide();
        await delay(1000);
        $(""#newDrink"").fadeOut(200);
        await delay(500);
        //switch statement for each choice
